import React, { useState, useRef, useEffect } from 'react';
import StatusBar from './components/StatusBar';
import MenuBar from './components/MenuBar';
import ContentWindow from './components/ContentWindow';
import MessageBar from './components/MessageBar';
import './App.css';

function App() {
  const [isMenuOpen, setMenuOpen] = useState(false);
  const [projects, setProjects] = useState([]);
  const menuBarRef = useRef();

  const handleMenuToggle = () => {
    setMenuOpen((prev) => !prev);
  };

  useEffect(() => {
    // Fetch presentation data from the backend (example API call)
    fetch('http://localhost:5000/api/presentations')
      .then((response) => response.json())
      .then((data) => {
        setProjects(data);
      })
      .catch((error) => {
        console.error('Error fetching presentations:', error);
      });
  }, []);

  useEffect(() => {
    if (menuBarRef.current && projects.length > 0) {
      updateMenuItems(projects);
    }
  }, [projects]);

  const updateMenuItems = (projects) => {
    if (menuBarRef.current) {
      menuBarRef.current.clearMenuItems();
      projects.forEach((project) => {
        const firstSlideName = project.slides[0]?.slideName || 'Untitled Slide';
        menuBarRef.current.addMenuItem(null, firstSlideName, null);
      });
    }
  };

  return (
    <div className="App">
      <StatusBar isMenuOpen={isMenuOpen} handleMenuToggle={handleMenuToggle} />
      <MenuBar ref={menuBarRef} isOpen={isMenuOpen} toggleMenu={handleMenuToggle} />
      <ContentWindow projects={projects} />
      <MessageBar />
    </div>
  );
}

export default App;
