// ChatBar.js - Manages the chat history and sliding behavior

import React, { useState, useEffect } from 'react';
import './ChatBar.css';

function ChatBar({ isOpen, toggleChat }) {
    const [chatHistory, setChatHistory] = useState([]);

    useEffect(() => {
        const event = new CustomEvent('chatBarToggle', { detail: { isOpen } });
        window.dispatchEvent(event);
    }, [isOpen]);

    // Function to add message to the chat history
    const addMessage = (publisher, message) => {
        setChatHistory([...chatHistory, { publisher, message }]);
    };

    return (
        <aside className={`chat-bar ${isOpen ? 'active' : ''}`}>
            <button className="close-button" onClick={toggleChat}>X</button>
            <div className="chat-history">
                {chatHistory.map((chat, index) => (
                    <div key={index} className="chat-message">
                        <strong>{chat.publisher}:</strong> {chat.message}
                    </div>
                ))}
            </div>
        </aside>
    );
}

export default ChatBar;

// Example of how to use addMessage:
// addMessage('User', 'Hello, this is a test message');
// addMessage('Chatbot', 'Hello User, how can I assist you today?');
