import React, { useState, useEffect } from 'react';
import StatusBar from './components/StatusBar';
import MenuBar from './components/MenuBar';
import ContentWindow from './components/ContentWindow';
import MessageBar from './components/MessageBar';
import './App.css';

function App() {
  const [isMenuOpen, setMenuOpen] = useState(false);
  const [menuItems, setMenuItems] = useState([]);

  const handleMenuToggle = () => {
    setMenuOpen((prev) => !prev);
  };

  useEffect(() => {
    // Example items to add to the menu
    setMenuItems([
      { logo: null, name: 'Content Title', extraInfo: null },
      { logo: null, name: 'User Name', extraInfo: 'User Info' },
      { logo: 'path/to/logo.png', name: 'User Name', extraInfo: 'User Info' }
    ]);
  }, []);

  return (
    <div className="App">
      <StatusBar isMenuOpen={isMenuOpen} handleMenuToggle={handleMenuToggle} />
      <MenuBar isOpen={isMenuOpen} toggleMenu={handleMenuToggle} items={menuItems} />
      <ContentWindow />
      <MessageBar />
    </div>
  );
}

export default App;
