import React, { useState, useRef } from 'react';
import StatusBar from './components/StatusBar';
import MenuBar from './components/MenuBar';
import ContentWindow from './components/ContentWindow';
import MessageBar from './components/MessageBar';
import './App.css';

function App() {
  const [isMenuOpen, setMenuOpen] = useState(false);
  const menuBarRef = useRef();

  const handleMenuToggle = () => {
    setMenuOpen((prev) => !prev);
  };

  // Add menu items when the menu is first opened
  const handleMenuButtonClick = () => {
    handleMenuToggle();
    if (!isMenuOpen && menuBarRef.current) {
      menuBarRef.current.addMenuItem(null, 'Content Title', null);
      menuBarRef.current.addMenuItem(null, 'User Name', 'User Info');
      menuBarRef.current.addMenuItem('path/to/logo.png', 'User Name', 'User Info');
    }
  };


  return (
    <div className="App">
      <StatusBar isMenuOpen={isMenuOpen} handleMenuToggle={handleMenuButtonClick} />
      <MenuBar ref={menuBarRef} isOpen={isMenuOpen} toggleMenu={handleMenuToggle} />
      <ContentWindow />
      <MessageBar />
    </div>
  );
}

export default App;
