import React, { useState, useEffect } from 'react';
import './ContentWindow.css';

function ContentWindow() {
    const [projects, setProjects] = useState([]);
    const [currentSlideIndex, setCurrentSlideIndex] = useState(0);
    const [currentProject, setCurrentProject] = useState(null);

    useEffect(() => {
        // Fetch presentation data from the Flask backend
        fetch('http://localhost:5000/api/presentations')
            .then((response) => {
                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }
                return response.json();
            })
            .then((data) => {
                console.log('Fetched Data:', data); // Log the fetched data to debug JSON structure
                // Transform the fetched data into the required format for projects
                const fetchedProjects = Object.entries(data).map(([key, slides]) => {
                    if (!Array.isArray(slides) || slides.length === 0) {
                        console.warn(`Invalid or empty slides for project: ${key}`);
                        return null;
                    }
                    return {
                        id: key,
                        title: slides[0]?.['Presentation Name'] || 'Untitled Presentation',
                        description: `This presentation contains ${slides.length} slides.`,
                        slides: slides.map(slide => ({
                            slideId: slide['Slide ID'],
                            slideName: slide['Slide Name'],
                            slideType: slide['Slide Type'],
                            slideInformation: slide['Slide Information'],
                            slideDescription: slide['Slide Description'],
                        }))
                    };
                }).filter(project => project !== null);
                setProjects(fetchedProjects);
            })
            .catch((error) => {
                console.error('Error fetching presentations:', error);
                alert('Error fetching presentations. Please check the console for more details.');
            });
    }, []);

    const handleTileClick = (project) => {
        // Set the selected project and reset the slide index
        setCurrentProject(project);
        setCurrentSlideIndex(0);
    };

    const handleNextSlide = () => {
        if (currentProject && currentSlideIndex < currentProject.slides.length - 1) {
            setCurrentSlideIndex(currentSlideIndex + 1);
        }
    };

    const handlePreviousSlide = () => {
        if (currentProject && currentSlideIndex > 0) {
            setCurrentSlideIndex(currentSlideIndex - 1);
        }
    };

    return (
        <main className="content-window">
            <section className="ai-presentation">
                {currentProject ? (
                    <div className="slide-navigation">
                        <button onClick={handlePreviousSlide} disabled={currentSlideIndex === 0}>Left</button>
                        <div className="slide-content">
                            <h2>{currentProject.slides[currentSlideIndex].slideName}</h2>
                            <p>{currentProject.slides[currentSlideIndex].slideDescription}</p>
                            <p>Type: {currentProject.slides[currentSlideIndex].slideType}</p>
                            <p>Information: {currentProject.slides[currentSlideIndex].slideInformation}</p>
                        </div>
                        <button onClick={handleNextSlide} disabled={currentSlideIndex === currentProject.slides.length - 1}>Right</button>
                    </div>
                ) : (
                    <p id="ai-text">Welcome! Click on a project tile to learn more about it.</p>
                )}
            </section>
            <section className="project-tiles">
                {projects.map((project) => (
                    <div key={project.id} onClick={() => handleTileClick(project)}>
                        <div className="tile" id={project.id} key={project.id}>
                            <h2>{project.title}</h2>
                            <p>{project.description}</p>
                        </div>
                    </div>
                ))}
            </section>
        </main>
    );
}

export default ContentWindow;
