// ContentWindow.js - Handles the display of project tiles and AI presentation

import React from 'react';
import './ContentWindow.css';

function ContentWindow({ addMenuItem }) {
    const projects = [
        { title: 'Website Project', description: 'A modern responsive website', type: 'web', image: 'path/to/website_image.png' },
        { title: 'CAD Design', description: 'A 3D CAD model of a hardware part', type: 'hardware', image: 'path/to/cad_image.png' },
        { title: 'Research Work', description: 'A published research paper', type: 'research', image: 'path/to/research_image.png' },
    ];

    const handleAddProject = (project) => {
        if (addMenuItem) {
            addMenuItem(project.image, project.title, project.description);
        }
    };

    return (
        <div className="content-window">
            <div className="ai-presentation">
                <p>AI presenting the selected work...</p>
            </div>
            <div className="tiles-container">
                {projects.map((project, index) => (
                    <div key={index} className="project-tile" onClick={() => handleAddProject(project)}>
                        <img src={project.image} alt={project.title} className="project-image" />
                        <div className="project-info">
                            <h3>{project.title}</h3>
                            <p>{project.description}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default ContentWindow;

// Example usage:
// <ContentWindow addMenuItem={addMenuItem} />
