import React, { useEffect, useImperativeHandle, forwardRef, useState } from 'react';
import './MenuBar.css';

const MenuBar = forwardRef(({ isOpen, toggleMenu }, ref) => {
    const [menuItems, setMenuItems] = useState([]);

    useImperativeHandle(ref, () => ({
        addMenuItem: (logo, name, extraInfo) => {
            setMenuItems((prevItems) => [...prevItems, { logo, name, extraInfo }]);
        },
        clearMenuItems: () => {
            setMenuItems([]);
        },
        updateMenuItems: (items) => {
            setMenuItems(items);
        }
    }));

    return (
        <nav className={`menu-bar ${isOpen ? 'open' : ''}`}>
            <ul>
                {menuItems.map((item, index) => (
                    <li key={index} className="menu-item">
                        {item.logo && <img src={item.logo} alt="logo" className="menu-item-logo" />}
                        <div className="menu-item-text">
                            <span className="menu-item-name">{item.name}</span>
                            {item.extraInfo && <span className="menu-item-extra">{item.extraInfo}</span>}
                        </div>
                    </li>
                ))}
            </ul>
        </nav>
    );
});

export default MenuBar;
