// StatusBar.js - Controls the status bar, including chat and menu button functionality

import React, { useState, useEffect } from 'react';
import ChatBar from './ChatBar';
import './StatusBar.css';

function StatusBar({ handleMenuToggle, appName = 'Miruyume' }) {
    const [isChatOpen, setChatOpen] = useState(false);
    const [statusBarWidth, setStatusBarWidth] = useState('100%');

    const handleChatToggle = () => {
        setChatOpen(prev => !prev);
    };

    useEffect(() => {
        const handleChatBarToggle = (event) => {
            setStatusBarWidth(event.detail.isOpen ? 'calc(100% - 400px)' : '100%');
        };

        window.addEventListener('chatBarToggle', handleChatBarToggle);

        return () => {
            window.removeEventListener('chatBarToggle', handleChatBarToggle);
        };
    }, []);

    return (
        <>
            <header className="status-bar" style={{ width: statusBarWidth }}>
                <button className="menu-button" onClick={handleMenuToggle}>&#9776;</button>
                <h1 className="app-name">{appName}</h1>
                <button className="chat-button" onClick={handleChatToggle}>&#128172;</button>
            </header>
            <ChatBar isOpen={isChatOpen} toggleChat={handleChatToggle} />
        </>
    );
}

export default StatusBar;

